import React from 'react'
import reactDom from 'react-dom';
import Card from './Card'
import classes from './ErrorModal.module.css'

const Backdrop = props =>{
  return <div className={classes.backdrop} onClick={props.onConfirm}></div>
};

const ModalOverlay = props =>{
  return (
  <Card classesName = {classes.modal}>
  <header className={classes.header}>
      <h2>{props.title}</h2>
  </header>
  <div className={classes.content}>
      <p>{props.message}</p>
  </div>
  <footer className={classes.actions}>
      <button onClick={props.onConfirm}>Okay</button>
  </footer>
</Card>
  )
}

const ErrorModal = (props) => {
  return (
    <React.Fragment>
      {reactDom.createPortal(<Backdrop onClick={props.onConfirm}/>,document.getElementById('backdrop-root'))}
      {reactDom.createPortal(<ModalOverlay title={props.title} message = {props.message} onConfirm={props.onConfirm}/>,document)}
    </React.Fragment>
  )
}

export default ErrorModal
